from pybricks.hubs import PrimeHub
from pybricks.parameters import Axis, Button, Direction, Port, Stop
from pybricks.pupdevices import Motor, Remote
from pybricks.tools import multitask, run_task, wait

# Set up all devices.
prime_hub = PrimeHub(top_side=Axis.Z, front_side=Axis.X)
arm = Motor(Port.A, Direction.CLOCKWISE)
rotation = Motor(Port.E, Direction.COUNTERCLOCKWISE)
remote_2 = Remote(timeout=None)

# Initialize variables.
movement = 0
rotation_speed = 0
arm_speed = 0

async def subtask():
    global arm_speed
    while True:
        await wait(0)
        # Add arm speed
        if Button.RIGHT_MINUS in remote_2.buttons.pressed():
            print('Add arm speed')
            print(arm_speed)
            arm_speed = arm_speed + 50
            arm.run(arm_speed)
        # Lower arm Speed
        if Button.RIGHT_PLUS in remote_2.buttons.pressed():
            print('Lower arm speed')
            print(arm_speed)
            arm_speed = arm_speed - 50
            arm.run(arm_speed)
        # Reset arm
        if Button.RIGHT in remote_2.buttons.pressed():
            print('Reset Arm')
            await arm.run_angle(500, 0 - arm.angle(), Stop.COAST)
        if Button.LEFT_PLUS in remote_2.buttons.pressed():
            print('raise')
            await rotation.run_angle(20, 10)
        if Button.LEFT_MINUS in remote_2.buttons.pressed():
            print('lower')
            await rotation.run_angle(20, -10)
        if Button.LEFT in remote_2.buttons.pressed():
            print('reset rotation')
            await rotation.run_angle(20, 0 - rotation.angle())
        await wait(60)

async def main():
    global arm_speed
    arm.run(0)
    await multitask(
        subtask(),
    )


run_task(main())