from pybricks.hubs import PrimeHub
from pybricks.parameters import Axis, Direction, Port, Stop
from pybricks.pupdevices import Motor
from pybricks.robotics import DriveBase
from pybricks.tools import multitask, run_task, wait

from utility import AccurateTurn

# Set up all devices.
prime_hub = PrimeHub(top_side=Axis.Z, front_side=Axis.X)
motor_2 = Motor(Port.C, Direction.CLOCKWISE)
motor_3 = Motor(Port.D, Direction.COUNTERCLOCKWISE)
drive_base = DriveBase(motor_3, motor_2, 62.4, 80)
left_motor = Motor(Port.B, Direction.COUNTERCLOCKWISE)
right_motor = Motor(Port.F, Direction.COUNTERCLOCKWISE)

async def main():
    drive_base.use_gyro(True)
    drive_base.settings(straight_speed=1000)
    drive_base.settings(straight_acceleration=500)
    await drive_base.straight(-10)
    drive_base.reset(0, 0)
    await drive_base.straight(250)
    await drive_base.turn(-90)
    await drive_base.straight(525)
    await drive_base.turn(90)
    drive_base.settings(straight_speed=500)
    drive_base.settings(straight_acceleration=250)
    await drive_base.straight(105)
    await left_motor.run_angle(350, -200)
    drive_base.settings(straight_acceleration=400)
    drive_base.settings(straight_speed=1000)
    await multitask(
        left_motor.run_angle(650, 200),
        drive_base.straight(-100),
    )
    await AccurateTurn(prime_hub, drive_base, 150)
    print('After turning to mission 11', prime_hub.imu.heading(), sep=", ")
    await drive_base.turn(150 - prime_hub.imu.heading())
    await drive_base.straight(80)
    await right_motor.run_angle(200, 300)
    await drive_base.straight(20)
    drive_base.settings(turn_rate=90)
    drive_base.settings(turn_acceleration=180)
    await drive_base.turn(90 - prime_hub.imu.heading())
    await multitask(
        right_motor.run_angle(200, -right_motor.angle()),
        drive_base.straight(600, then=Stop.COAST),
    )
    drive_base.stop()


run_task(main())