from pybricks.hubs import PrimeHub
from pybricks.iodevices import XboxController
from pybricks.parameters import Axis, Button, Direction, Port, Stop
from pybricks.pupdevices import Motor
from pybricks.robotics import DriveBase
from pybricks.tools import multitask, run_task, wait

# Set up all devices.
prime_hub = PrimeHub(top_side=Axis.Z, front_side=Axis.X)
rightsheel = Motor(Port.C, Direction.CLOCKWISE)
leftwheel = Motor(Port.D, Direction.COUNTERCLOCKWISE)
drive_base = DriveBase(leftwheel, rightsheel, 62.4, 114)
left_motor = Motor(Port.B, Direction.COUNTERCLOCKWISE)
right_motor = Motor(Port.F, Direction.COUNTERCLOCKWISE)
controller = XboxController()

async def main1():
    print(prime_hub.battery.voltage())
    while True:
        await wait(0)
        if Button.A in controller.buttons.pressed():
            await right_motor.run_angle(500, -90)
        elif Button.B in controller.buttons.pressed():
            await right_motor.run_angle(500, 90)
        elif Button.X in controller.buttons.pressed():
            await left_motor.run_angle(500, -90)
        elif Button.Y in controller.buttons.pressed():
            await left_motor.run_angle(500, 90)
        else:
            await wait(25)

async def main2():
    left_motor.reset_angle(0)
    right_motor.reset_angle(0)
    while True:
        await wait(0)
        if Button.LB in controller.buttons.pressed():
            print(left_motor.angle())
            print(right_motor.angle())
        elif Button.RB in controller.buttons.pressed():
            print(drive_base.distance())
            print(drive_base.angle())
        else:
            pass
        await wait(300)

async def main3():
    while True:
        await wait(0)
        if Button.UP in controller.buttons.pressed():
            await drive_base.straight(50)
        elif Button.DOWN in controller.buttons.pressed():
            await drive_base.straight(50)
        elif Button.LEFT in controller.buttons.pressed():
            await drive_base.arc(-100, angle=30)
        elif Button.RIGHT in controller.buttons.pressed():
            await drive_base.arc(100, angle=30)
        else:
            await wait(25)


async def main():
    await multitask(main1(), main2(), main3())

run_task(main())