from pybricks.hubs import PrimeHub
from pybricks.parameters import Axis, Direction, Port, Stop
from pybricks.pupdevices import Motor
from pybricks.robotics import DriveBase
from pybricks.tools import multitask, run_task, wait

# Set up all devices.
prime_hub = PrimeHub(top_side=Axis.Z, front_side=Axis.X)
rightwheel = Motor(Port.C, Direction.CLOCKWISE)
leftwheel = Motor(Port.D, Direction.COUNTERCLOCKWISE)
drive_base = DriveBase(leftwheel, rightwheel, 62.4, 80)
left_motor = Motor(Port.B, Direction.COUNTERCLOCKWISE)
right_motor = Motor(Port.F, Direction.COUNTERCLOCKWISE)

# Initialize variables.
heading = 0
delta_heading = 0
repeated = 0

async def subtask():
    await right_motor.run_angle(200, 150)
    await wait(500)

async def Accurate_Turn(Angle):
    global repeated, delta_heading
    await wait(0)
    repeated = 0
    delta_heading = Angle - prime_hub.imu.heading()
    await drive_base.turn(1 * delta_heading, then=Stop.COAST)
    delta_heading = Angle - prime_hub.imu.heading()
    while not -2 <= delta_heading <= 2:
        await wait(0)
        repeated = repeated + 1
        await drive_base.turn(1.5 * delta_heading, then=Stop.COAST)
        delta_heading = Angle - prime_hub.imu.heading()
        if repeated > 3:
            break

async def main():
    global heading
    drive_base.use_gyro(True)
    drive_base.settings(straight_speed=600)
    drive_base.settings(straight_acceleration=150)
    drive_base.settings(turn_rate=90)
    drive_base.settings(turn_acceleration=100)
    # Backward drive to mission 03
    await multitask(
        left_motor.run_angle(1000, 400),
        right_motor.run_angle(500, 30),
        drive_base.straight(-743),
    )
    await Accurate_Turn(-90)
    heading = prime_hub.imu.heading()
    print(heading)
    prime_hub.imu.reset_heading(0)
    await drive_base.straight(150)
    # Left arm picks up precious artifacts. Right arm lifts minecart.
    await multitask(
        left_motor.run_angle(500, 450),
        subtask(),
    )
    await drive_base.straight(-150)
    await multitask(
        drive_base.turn(40),
        left_motor.run_angle(1500, 1080),
        right_motor.run_angle(500, 200),
    )
    await drive_base.straight(-65)
    await right_motor.run_angle(200, -100)
    await drive_base.straight(30)
    await drive_base.turn(60, then=Stop.COAST)
    drive_base.settings(straight_speed=1000)
    drive_base.settings(straight_acceleration=500)
    await drive_base.straight(900)
    raise SystemExit


run_task(main())