from pybricks.hubs import PrimeHub
from pybricks.parameters import Axis, Direction, Port, Stop
from pybricks.pupdevices import Motor
from pybricks.robotics import DriveBase
from pybricks.tools import StopWatch, multitask, run_task, wait

from utility import AccurateTurn

# Set up all devices.
prime_hub = PrimeHub(top_side=Axis.Z, front_side=Axis.X)
watch_2 = StopWatch()
rightsheel = Motor(Port.C, Direction.CLOCKWISE)
leftwheel = Motor(Port.D, Direction.COUNTERCLOCKWISE)
drive_base = DriveBase(leftwheel, rightsheel, 62.4, 114)
left_motor = Motor(Port.B, Direction.COUNTERCLOCKWISE)
right_motor = Motor(Port.F, Direction.COUNTERCLOCKWISE)

async def subtask():
    await wait(300)
    await drive_base.straight(150)

async def Move_to(X_amount, Y_amount):
    global X_pos, Y_pos
    await wait(0)
    await multitask(
        X.run_angle(300, ((X_pos - X_amount) * 9) * -1),
        Y.straight((Y_pos - Y_amount) * -1, then=Stop.COAST),
    )
    X_pos = X_amount
    Y_pos = Y_amount

async def main():
    # William Chen
    drive_base.reset(0, 0)
    watch_2.reset()
    print(prime_hub.battery.voltage())
    await left_motor.run_angle(500, -10)
    await right_motor.run_angle(500, 10)
    drive_base.use_gyro(True)
    await drive_base.straight(760)
    # Remove heavy ores
    await right_motor.run_angle(250, -200)
    await wait(10)
    # Reset motor
    await right_motor.run_angle(500, 200)
    await wait(10)
    # flip table
    await multitask(
        left_motor.run_angle(500, 150),
        wait(1000),
        race=True,
    )
    await left_motor.run_angle(500, -150)
    await wait(10)
    # Back out
    await drive_base.straight(-40)
    await drive_base.straight(720 - drive_base.distance())
    print('before turn')
    print(drive_base.angle())
    await AccurateTurn(prime_hub, drive_base, -105 - drive_base.angle())
    print('after turn')
    print(drive_base.angle())
    await drive_base.straight(-350)
    await wait(500)
    await drive_base.straight(-200)
    await drive_base.arc(60, angle=60)
    print('after back')
    print(drive_base.angle())
    await multitask(
        left_motor.run_angle(500, 100),
        subtask(),
    )
    await multitask(
        left_motor.run_angle(500, -100),
        wait(700),
        race=True,
    )
    await drive_base.straight(-150)
    print(watch_2.time() / 1000)
    for item in [12, 8, 5, 3, 4, 5, 4, 16]:
        await wait(0)
        await prime_hub.speaker.beep(12 * item + 205, 200)


run_task(main())