from pybricks.hubs import PrimeHub
from pybricks.parameters import Axis, Direction, Port, Stop
from pybricks.pupdevices import Motor
from pybricks.robotics import DriveBase

# Set up all devices.
prime_hub = PrimeHub(top_side=Axis.Z, front_side=Axis.X)
motor_2 = Motor(Port.C, Direction.CLOCKWISE)
motor_3 = Motor(Port.D, Direction.COUNTERCLOCKWISE)
drive_base_2 = DriveBase(motor_3, motor_2, 62.4, 80)
left_motor = Motor(Port.B, Direction.COUNTERCLOCKWISE)
right_motor = Motor(Port.F, Direction.COUNTERCLOCKWISE)


# The main program starts here.
drive_base_2.use_gyro(True)
drive_base_2.settings(straight_speed=1000)
drive_base_2.straight(250)
drive_base_2.turn(-90)
drive_base_2.straight(525)
drive_base_2.turn(90)
drive_base_2.straight(115)
left_motor.run_angle(650, -200)
left_motor.run_angle(650, 200)
drive_base_2.straight(-110)
drive_base_2.turn(90)
drive_base_2.straight(-105)
drive_base_2.turn(35)
drive_base_2.straight(40)
drive_base_2.straight(25)
right_motor.run_angle(400, 300)
drive_base_2.turn(-60)
drive_base_2.straight(450)
drive_base_2.turn(50)
drive_base_2.settings(straight_speed=1000)
drive_base_2.arc(100, angle=90)
