from pybricks.hubs import PrimeHub
from pybricks.parameters import Axis, Direction, Port, Stop
from pybricks.pupdevices import Motor
from pybricks.robotics import DriveBase
from pybricks.tools import multitask, run_task, wait

# Set up all devices.
prime_hub = PrimeHub(top_side=Axis.Z, front_side=Axis.X)
rightsheel = Motor(Port.C, Direction.CLOCKWISE)
leftwheel = Motor(Port.D, Direction.COUNTERCLOCKWISE)
drive_base = DriveBase(leftwheel, rightsheel, 62.4, 114)
left_motor = Motor(Port.B, Direction.COUNTERCLOCKWISE)
right_motor = Motor(Port.F, Direction.COUNTERCLOCKWISE)

async def subtask():
    await wait(500)
    await left_motor.run_angle(500, -70)

async def subtask2():
    await left_motor.run_angle(500, 30)
    await right_motor.run_angle(500, 50)

async def main():
    drive_base.reset(0, 0)
    await left_motor.run_angle(500, 10)
    await right_motor.run_angle(500, 10)
    drive_base.use_gyro(True)
    await drive_base.straight(620)
    await multitask(
        subtask(),
        right_motor.run_angle(500, -170),
    )
    await drive_base.straight(135)
    await multitask(
        left_motor.run_angle(500, 40),
        right_motor.run_angle(500, 20),
    )
    await multitask(
        drive_base.turn(-60),
        wait(400),
        race=True,
    )
    await drive_base.straight(-40)
    await drive_base.turn(-68)
    while not -80 >= drive_base.angle():
        await wait(0)
        await drive_base.turn(-1)
    await multitask(
        drive_base.straight(-300),
        subtask2(),
    )
    await wait(800)
    await drive_base.straight(-225)
    await drive_base.turn(60)
    while not -40 <= drive_base.angle():
        await wait(0)
        await drive_base.turn(1)
    await left_motor.run_angle(500, 10)
    await drive_base.straight(160)
    await drive_base.turn(90)
    await drive_base.straight(10)
    await drive_base.turn(-50)


run_task(main())