from pybricks.hubs import PrimeHub
from pybricks.parameters import Axis, Direction, Port, Stop
from pybricks.pupdevices import Motor
from pybricks.robotics import DriveBase
from pybricks.tools import multitask, run_task, wait

from utility import AccurateTurn

# Set up all devices.
prime_hub = PrimeHub(top_side=Axis.Z, front_side=Axis.X)
rightsheel = Motor(Port.C, Direction.CLOCKWISE)
leftwheel = Motor(Port.D, Direction.COUNTERCLOCKWISE)
drive_base = DriveBase(leftwheel, rightsheel, 62.4, 114)
left_motor = Motor(Port.B, Direction.COUNTERCLOCKWISE)
right_motor = Motor(Port.F, Direction.COUNTERCLOCKWISE)

async def subtask():
    await wait(400)
    await left_motor.run_angle(500, -100)

async def subtask2():
    await left_motor.run_angle(500, -40)
    await drive_base.straight(-200)

async def main():
    drive_base.reset(0, 0)
    print(prime_hub.battery.voltage())
    await left_motor.run_angle(500, 10)
    await right_motor.run_angle(500, 10)
    drive_base.use_gyro(True)
    await drive_base.straight(620)
    await multitask(
        subtask(),
        right_motor.run_angle(500, -170),
        drive_base.straight(135),
    )
    await right_motor.run_angle(750, 200)
    await wait(10)
    await right_motor.run_angle(500, -100)
    await wait(10)
    await multitask(
        left_motor.run_angle(500, 120),
        wait(500),
        race=True,
    )
    await left_motor.run_angle(500, -60)
    await wait(10)
    await drive_base.straight(-40)
    await AccurateTurn(prime_hub, drive_base, -1 * (100 - drive_base.angle()))
    await drive_base.straight(-350)
    await multitask(
        subtask2(),
        wait(300),
    )
    await drive_base.turn(70)
    await left_motor.run_angle(500, 70)
    await right_motor.run_angle(500, -80)
    await drive_base.straight(100)
    await drive_base.turn(90)
    await drive_base.straight(50)
    for item in [12, 8, 5, 3, 4, 5, 4, 16]:
        await wait(0)
        await prime_hub.speaker.beep(12 * item + 205, 200)


run_task(main())