from pybricks.hubs import PrimeHub
from pybricks.parameters import Axis, Direction, Port, Stop
from pybricks.pupdevices import Motor
from pybricks.robotics import DriveBase
from pybricks.tools import StopWatch, multitask, run_task, wait

# Set up all devices.
prime_hub = PrimeHub(top_side=Axis.Z, front_side=Axis.X)
watch = StopWatch()
rightsheel = Motor(Port.C, Direction.CLOCKWISE)
leftwheel = Motor(Port.D, Direction.COUNTERCLOCKWISE)
drive_base = DriveBase(leftwheel, rightsheel, 62.4, 114)
left_motor = Motor(Port.B, Direction.COUNTERCLOCKWISE)
right_motor = Motor(Port.F, Direction.COUNTERCLOCKWISE)

async def subtask():
    await left_motor.run_until_stalled(-500, Stop.COAST, 50)
    left_motor.reset_angle(0)

async def subtask2():
    await right_motor.run_until_stalled(500, Stop.COAST, 60)
    right_motor.reset_angle(0)

async def subtask3():
    await drive_base.straight(-10)
    drive_base.reset(0, 0)

async def subtask4():
    await wait(0)
    await right_motor.run_target(1000, 0)

async def subtask5():
    await wait(0)
    await right_motor.run_target(1000, 0)

async def subtask6():
    await multitask(
        subtask4(),
        left_motor.run_angle(500, 110),
    )

async def subtask7():
    await wait(5000)
    print('Lift millstone timeout')

async def Move_to(X_amount, Y_amount):
    global X_pos, Y_pos
    await wait(0)
    await multitask(
        X.run_angle(300, ((X_pos - X_amount) * 9) * -1),
        Y.straight((Y_pos - Y_amount) * -1, then=Stop.COAST),
    )
    X_pos = X_amount
    Y_pos = Y_amount

async def main():
    # William Chen
    watch.reset()
    print('Battery', prime_hub.battery.voltage(), sep=", ")
    await multitask(
        subtask(),
        subtask2(),
        subtask3(),
    )
    drive_base.use_gyro(True)
    drive_base.settings(straight_speed=800)
    drive_base.settings(straight_acceleration=800)
    await drive_base.straight(760)
    # Heavy lifting
    await right_motor.run_angle(1000, -1000, Stop.COAST)
    await multitask(
        subtask6(),
        subtask7(),
        race=True,
    )
    # flip table
    await wait(10)
    # Release ores
    await drive_base.turn(50)
    await left_motor.run_angle(500, -100)
    await wait(50)
    await drive_base.straight(-50)
    await wait(50)
    drive_base.settings(turn_rate=360)
    drive_base.settings(turn_acceleration=750)
    await multitask(
        drive_base.turn(-90 - prime_hub.imu.heading()),
        left_motor.run_until_stalled(-1000, Stop.COAST, 50),
    )
    await drive_base.straight(-470)
    await multitask(
        right_motor.run_angle(500, -500),
        drive_base.turn(5 - prime_hub.imu.heading()),
    )


run_task(main())