from pybricks.hubs import PrimeHub
from pybricks.parameters import Axis, Direction, Port, Stop
from pybricks.pupdevices import Motor
from pybricks.robotics import DriveBase
from pybricks.tools import multitask, run_task, wait

# Set up all devices.
prime_hub = PrimeHub(top_side=Axis.Z, front_side=Axis.X)
rightWheel = Motor(Port.C, Direction.CLOCKWISE)
leftWheel = Motor(Port.D, Direction.COUNTERCLOCKWISE)
drive_base = DriveBase(leftWheel, rightWheel, 62.5, 80)
right_motor = Motor(Port.F, Direction.COUNTERCLOCKWISE)
left_motor = Motor(Port.B, Direction.COUNTERCLOCKWISE)

async def subtask():
    await wait(1000)
    await right_motor.run_angle(500, -200)

async def main():
    drive_base.settings(straight_speed=300)
    drive_base.settings(straight_acceleration=300)
    await right_motor.run_until_stalled(-500, Stop.COAST, 50)
    right_motor.reset_angle(0)
    drive_base.use_gyro(True)
    drive_base.reset(0, 0)
    await multitask(
        drive_base.straight(480),
        wait(2000),
        race=True,
    )
    await right_motor.run_angle(500, 250)
    await wait(500)
    await drive_base.turn(-prime_hub.imu.heading())
    drive_base.settings(straight_speed=800)
    drive_base.settings(straight_acceleration=400)
    await multitask(
        drive_base.straight(-500),
        subtask(),
    )
    drive_base.stop()


run_task(main())