from pybricks.hubs import PrimeHub
from pybricks.parameters import Axis, Direction, Port, Stop
from pybricks.pupdevices import Motor
from pybricks.robotics import DriveBase
from pybricks.tools import multitask, run_task, wait

# Set up all devices.
prime_hub = PrimeHub(top_side=Axis.Z, front_side=Axis.X)
right_wheel = Motor(Port.C, Direction.CLOCKWISE)
left_wheel = Motor(Port.D, Direction.COUNTERCLOCKWISE)
drive_base = DriveBase(left_wheel, right_wheel, 62.4, 80)
left_motor = Motor(Port.B, Direction.COUNTERCLOCKWISE)
right_motor = Motor(Port.F, Direction.COUNTERCLOCKWISE)

async def main():
    await multitask(
        left_motor.run_until_stalled(500, Stop.HOLD, 50),
        right_motor.run_until_stalled(500, Stop.HOLD, 50),
        drive_base.straight(-10),
    )
    drive_base.use_gyro(True)
    left_motor.reset_angle(0)
    right_motor.reset_angle(0)
    drive_base.reset(0, 0)
    await drive_base.straight(457)
    for count in range(3):
        await wait(0)
        await right_motor.run_angle(20000, -270, Stop.COAST)
        await wait(500)
        await right_motor.run_angle(300, -right_motor.angle())
        await wait(300)
        drive_base.use_gyro(True)
        await drive_base.turn(-prime_hub.imu.heading())
    await drive_base.straight(-235)
    drive_base.use_gyro(True)
    await drive_base.turn(-37 - prime_hub.imu.heading())
    await drive_base.straight(235, then=Stop.COAST)
    drive_base.settings(turn_rate=360)
    drive_base.settings(turn_acceleration=180)
    await drive_base.turn(-44, then=Stop.BRAKE)
    await drive_base.turn(44, then=Stop.BRAKE)
    await left_motor.run_until_stalled(-300, Stop.COAST, 50)
    await multitask(
        drive_base.straight(-149),
        left_motor.run_angle(50, 50),
    )
    await multitask(
        drive_base.straight(100),
        left_motor.run_angle(500, -left_motor.angle()),
    )
    drive_base.settings(straight_acceleration=400)
    drive_base.settings(straight_speed=400)
    await drive_base.straight(-550)
    drive_base.stop()


run_task(main())