from pybricks.hubs import PrimeHub
from pybricks.parameters import Axis, Direction, Port, Stop
from pybricks.pupdevices import Motor
from pybricks.robotics import DriveBase
from pybricks.tools import StopWatch, multitask, run_task, wait

from utility import turn_by_wheel

# Set up all devices.
prime_hub = PrimeHub(top_side=Axis.Z, front_side=Axis.X)
watch = StopWatch()
right_wheel = Motor(Port.C, Direction.CLOCKWISE)
left_wheel = Motor(Port.D, Direction.COUNTERCLOCKWISE)
drive_base = DriveBase(left_wheel, right_wheel, 62.4, 80)
left_motor = Motor(Port.B, Direction.COUNTERCLOCKWISE)
right_motor = Motor(Port.F, Direction.COUNTERCLOCKWISE)

async def subtask():
    await right_motor.run_until_stalled(-500, Stop.HOLD, 50)
    right_motor.reset_angle(0)

async def subtask2():
    await drive_base.straight(-15)
    drive_base.reset(0, 0)
    drive_base.settings(straight_speed=600)
    drive_base.settings(straight_acceleration=300)
    await drive_base.straight(730)

async def subtask3():
    await right_motor.run_angle(500, 75)
    await right_motor.run_angle(500, -75)

async def subtask4():
    await left_motor.run_angle(300, 125)
    await wait(200)
    await left_motor.run_angle(300, -125)

async def main():
    watch.reset()
    drive_base.use_gyro(True)
    await multitask(
        subtask(),
        subtask2(),
    )
    await drive_base.turn(42 - prime_hub.imu.heading())
    await turn_by_wheel(prime_hub, drive_base, left_wheel, right_wheel, 42)
    await right_motor.run_angle(500, 210)
    await drive_base.straight(290)
    await drive_base.straight(-15)
    await right_motor.run_angle(500, -150)
    await multitask(
        subtask3(),
        subtask4(),
    )
    await drive_base.straight(-180)
    drive_base.settings(turn_rate=360)
    drive_base.settings(turn_acceleration=750)
    await drive_base.turn(0 - prime_hub.imu.heading())
    drive_base.settings(straight_speed=1000)
    drive_base.settings(straight_acceleration=1000)
    await drive_base.arc(-1200, distance=1100)
    print('Deliver time', watch.time(), sep=", ")
    print('Battery', prime_hub.battery.voltage(), sep=", ")
    drive_base.stop()


run_task(main())