from pybricks.hubs import PrimeHub
from pybricks.parameters import Axis, Direction, Port, Stop
from pybricks.pupdevices import Motor
from pybricks.robotics import DriveBase
from pybricks.tools import multitask, run_task, wait

# Set up all devices.
prime_hub = PrimeHub(top_side=Axis.Z, front_side=Axis.X)
leftwheel = Motor(Port.D, Direction.COUNTERCLOCKWISE)
rightwheel = Motor(Port.C, Direction.CLOCKWISE)
leftattachment = Motor(Port.B, Direction.COUNTERCLOCKWISE)
rightattachment = Motor(Port.F, Direction.COUNTERCLOCKWISE)
drive_base = DriveBase(leftwheel, rightwheel, 62.4, 80)

async def subtask():
    drive_base.settings(straight_speed=300)
    await drive_base.straight(750)
    drive_base.settings(straight_speed=200)

async def main():
    drive_base.use_gyro(True)
    await multitask(
        subtask(),
        leftattachment.run_angle(500, 20),
    )
    drive_base.settings(turn_rate=60)
    await drive_base.turn(-45)
    await multitask(
        drive_base.straight(215),
        wait(1500),
        race=True,
    )
    await rightattachment.run_angle(500, -270)
    await drive_base.straight(-70)
    await drive_base.turn(45)
    await drive_base.straight(-65)
    await drive_base.turn(-90)
    await drive_base.straight(100)
    leftattachment.control.limits(acceleration=1500)
    await leftattachment.run_angle(500, 90)
    await drive_base.straight(-50)
    await drive_base.turn(90)
    drive_base.settings(straight_speed=500)
    await drive_base.straight(-700)


run_task(main())