from pybricks.hubs import PrimeHub
from pybricks.parameters import Axis, Direction, Port, Stop
from pybricks.pupdevices import Motor
from pybricks.robotics import DriveBase
from pybricks.tools import StopWatch, multitask, run_task, wait

from utility import steer_turn

# Set up all devices.
prime_hub = PrimeHub(top_side=Axis.Z, front_side=Axis.X)
watch = StopWatch()
rightsheel = Motor(Port.C, Direction.CLOCKWISE)
leftwheel = Motor(Port.D, Direction.COUNTERCLOCKWISE)
drive_base = DriveBase(leftwheel, rightsheel, 62.4, 80)
left_motor = Motor(Port.B, Direction.COUNTERCLOCKWISE)
right_motor = Motor(Port.F, Direction.COUNTERCLOCKWISE)

async def subtask():
    await left_motor.run_until_stalled(500, Stop.HOLD, 50)
    left_motor.reset_angle(0)

async def subtask2():
    await right_motor.run_until_stalled(500, Stop.HOLD, 50)
    right_motor.reset_angle(0)

async def subtask3():
    await drive_base.straight(-10)
    drive_base.reset(0, 0)
    drive_base.use_gyro(True)

async def main():
    watch.reset()
    print('Battery', prime_hub.battery.voltage(), sep=", ")
    drive_base.use_gyro(True)
    await multitask(
        subtask(),
        subtask2(),
        subtask3(),
    )
    drive_base.settings(straight_speed=300)
    await drive_base.straight(405)
    await drive_base.turn(-43)
    await drive_base.turn(50)
    await left_motor.run_until_stalled(-200, Stop.HOLD, 50)
    await multitask(
        drive_base.straight(-143),
        left_motor.run_angle(40, 50),
    )
    await multitask(
        drive_base.straight(45),
        left_motor.run_target(200, 0),
    )
    await drive_base.arc(200, distance=-190)
    await drive_base.straight(520, then=Stop.NONE)
    await steer_turn(prime_hub, leftwheel, rightsheel, 45, True, 500, 3)
    await drive_base.straight(100)
    drive_base.settings(straight_speed=300)
    drive_base.settings(straight_acceleration=300)
    await wait(500)
    await drive_base.straight(-120, then=Stop.NONE)
    drive_base.settings(straight_speed=600)
    drive_base.settings(straight_acceleration=800)
    drive_base.settings(turn_rate=360)
    drive_base.settings(turn_acceleration=720)
    await drive_base.turn(80, then=Stop.NONE)
    await drive_base.straight(600)
    print('Scale time', watch.time())


run_task(main())