from pybricks.hubs import PrimeHub
from pybricks.parameters import Axis, Direction, Port, Stop
from pybricks.pupdevices import Motor
from pybricks.robotics import DriveBase
from pybricks.tools import StopWatch, multitask, run_task, wait

# Set up all devices.
prime_hub = PrimeHub(top_side=Axis.Z, front_side=Axis.X)
watch = StopWatch()
RightWheel = Motor(Port.C, Direction.CLOCKWISE)
LeftWheel = Motor(Port.D, Direction.COUNTERCLOCKWISE)
DriveBase = DriveBase(LeftWheel, RightWheel, 62.5, 80)
RightMotor = Motor(Port.F, Direction.COUNTERCLOCKWISE)
LeftMotor = Motor(Port.B, Direction.COUNTERCLOCKWISE)

async def subtask():
    await wait(3000)
    print('drive 450 timeout')

async def subtask2():
    await wait(1500)
    print('drive 170 timeout')

async def subtask3():
    await LeftMotor.run_until_stalled(500, Stop.HOLD, 80)
    await DriveBase.straight(-50)
    await DriveBase.straight(50)
    await RightMotor.run_angle(600, -1200)

async def subtask4():
    await DriveBase.straight(-50, then=Stop.NONE)
    await DriveBase.turn(5)

async def main():
    watch.reset()
    DriveBase.settings(straight_speed=300)
    DriveBase.settings(straight_acceleration=400)
    await multitask(
        LeftMotor.run_until_stalled(-1000, Stop.COAST, 50),
        DriveBase.straight(-10),
    )
    DriveBase.use_gyro(True)
    DriveBase.reset(0, 0)
    print(prime_hub.battery.voltage())
    await multitask(
        DriveBase.straight(460),
        subtask(),
        race=True,
    )
    # Pull sand backward
    await DriveBase.straight(-130)
    await multitask(
        DriveBase.straight(160),
        subtask2(),
        race=True,
    )
    # Rotate to raise artifact
    await multitask(
        DriveBase.turn(5),
        subtask3(),
    )
    print('Heading after raise artifact', prime_hub.imu.heading())
    await multitask(
        subtask4(),
        LeftMotor.run_angle(500, -800),
    )
    DriveBase.settings(straight_speed=600)
    DriveBase.settings(straight_acceleration=750)
    print('Heading before backing', prime_hub.imu.heading())
    DriveBase.use_gyro(False)
    await DriveBase.straight(-800)
    print('Heading after backing', prime_hub.imu.heading())
    print('run time', watch.time(), sep=", ")


run_task(main())