from pybricks.hubs import PrimeHub
from pybricks.parameters import Axis, Direction, Port, Stop
from pybricks.pupdevices import Motor
from pybricks.robotics import DriveBase
from pybricks.tools import multitask, run_task, wait

# Set up all devices.
prime_hub = PrimeHub(top_side=Axis.Z, front_side=Axis.X)
RightWheel = Motor(Port.C, Direction.CLOCKWISE)
LeftWheel = Motor(Port.D, Direction.COUNTERCLOCKWISE)
DriveBase = DriveBase(LeftWheel, RightWheel, 62.5, 80)
RightMotor = Motor(Port.F, Direction.COUNTERCLOCKWISE)
LeftMotor = Motor(Port.B, Direction.COUNTERCLOCKWISE)

async def subtask():
    await DriveBase.straight(170)
    DriveBase.reset(0, 0)

async def subtask2():
    await wait(1000)
    await LeftMotor.run_angle(500, 800)
    await DriveBase.turn(20)
    await DriveBase.straight(-50)
    await wait(500)
    await DriveBase.straight(30)
    await RightMotor.run_angle(600, -1000)

async def subtask3():
    for count in range(4):
        await wait(0)
        await prime_hub.speaker.beep(500, 200)
        await prime_hub.speaker.beep(300, 150)
        await prime_hub.speaker.beep(600, 130)
        await prime_hub.speaker.beep(100, 200)

async def main():
    DriveBase.settings(straight_speed=270)
    await multitask(
        LeftMotor.run_until_stalled(-1000, Stop.COAST, 50),
        DriveBase.straight(-10),
    )
    DriveBase.use_gyro(True)
    DriveBase.reset(0, 0)
    print(prime_hub.battery.voltage())
    await multitask(
        DriveBase.straight(440),
        wait(3000),
        race=True,
    )
    await DriveBase.straight(-120)
    await multitask(
        subtask(),
        subtask2(),
    )
    await DriveBase.straight(-60)
    await LeftMotor.run_angle(500, -500)
    DriveBase.settings(straight_speed=200)
    await multitask(
        DriveBase.straight(-1000),
        subtask3(),
    )


run_task(main())